package prueba;

public class entornos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double a=5, b=7;
		int division=0, multiplicacion=0;
		
		division = (int) (a/b);
		multiplicacion = (int) (a*b);
		
		System.out.println("El valor de x es:  " + a);
		System.out.println("El valor de y es:  " + b);
		System.out.println("El valor de la division es:  " + division);
		System.out.println("El valor de multiplicacion es:  " + multiplicacion);

	}

}
